if (msg.payload != undefined && msg.payload.length != undefined && msg.payload.length > 0) {
    let li = msg.payload;
    let first_state = "";
    let newmsg = {};
    li.sort((x, y) => {
        return new Date(x.last_updated) < new Date(y.last_updated) ? 1 : -1
    })
    let states_only = [];
    for (var event of li) {
        if (event.state == "PREPARE") {
            continue;
        }
        if (first_state == "") {
            first_state == event.state;
            states_only.push(event.state);
        }
        else if (first_state != event.state) {
            states_only.push(event.state);
        }

    }
    newmsg.current_state = states_only[0];
    newmsg.previous_state = states_only[0];
    if (states_only.length > 1) {
        newmsg.previous_state = states_only[1];
    }
    node.send(newmsg);
}

if (msg.previous_state != undefined && msg.current_state != undefined
    && msg.previous_state != msg.current_state
    && msg.previous_state != "FINISH" && msg.previous_state != "FAILED") {
    msg.payload = msg.current_state;
    if(msg.previous_state == "PAUSE" && msg.current_state == "RUNNING") {
        node.status({ fill: "yellow", shape: "ring", text: "Print Resumed" });
    }
    else {

        if(msg.previous_state.toLowerCase() != "unknown"
        && msg.previous_state.toLowerCase() != "unavailable"
        && msg.previous_state != "OFFLINE" && msg.current_state != "OFFLINE"
        && msg.current_state.toLowerCase() != "unknown"
        && msg.current_state.toLowerCase() != "unavailable")
        {
            if (msg.previous_state != "PAUSE" && msg.current_state == "PAUSE"){
                // paused
                node.send(msg);
                node.status({ fill: "blue", shape: "ring", text: "Print Paused" });
            }
            else if (msg.previous_state != "FAILED" && msg.current_state == "FAILED") {
                // failed or cancelled
                node.send(msg);
                node.status({ fill: "blue", shape: "ring", text: "Print Cancelled or Failed" });
            }
            else if (msg.previous_state != "FINISH" && msg.current_state == "FINISH") {
                // finished
                node.send(msg);
                node.status({ fill: "blue", shape: "ring", text: "Print Completed" });
            }
            else if (msg.current_state == "RUNNING"){
                // starts
                node.status({ fill: "yellow", shape: "ring", text: "Print Started" });
            }
        }
    }
} else {
    node.status({ fill: "white", shape: "ring", text: "Unhandled: " + msg.previous_state + " -> " + msg.current_state });
}

