let prefixes = ["", "/cache", "/sdcard", "/sdcard/cache"];
if (msg.payload == "" || msg.payload == undefined ){
    msg.directory_count += 1;

    if (msg.directory_count >= prefixes.length) {
        node.send([null, msg]);
        node.status({ fill: "blue", shape: "ring", text: "Done Listing " + ` - ${dt}` });
    }
    else {
        node.send([msg, null]);
        node.status({ fill: "yellow", shape: "ring", text: "Listing... " + ` - ${dt}` });
    }
    return;
}

let x = msg.payload.split("\n")
if (msg.files == undefined) {
    msg.files = [];
}

if (msg.directory_count == undefined) {
    msg.directory_count = 0;
}

let r = /\d+ \w+ \d+\s(\d+:\d+|\s\d{4}) (.*\.3mf)/;
for (var a of x){
    if (a.endsWith(".3mf")){
        let f = a.match(r);
        if (f != undefined && f.length > 2) {
            let filename = f[2];
            if (!filename.startsWith("/")) {
                filename = "/"+filename;
            }
            let pre = prefixes[msg.directory_count];
            if (!filename.startsWith(pre) && pre != "") {
                filename = pre + filename;
            }
            msg.files.push(filename); 
        }
    }
}
msg.directory_count += 1;

if (msg.directory_count >= prefixes.length ) {
    node.status({ fill: "blue", shape: "ring", text: "Done Listing " + ` - ${dt}` });
    node.send([null, msg]);
}
else {
    node.status({ fill: "yellow", shape: "ring", text: "Listing... " + ` - ${dt}` });
    node.send([msg, null]);
}

let ip = flow.get("printer_real_ip");
let pw = flow.get("access_code");

if (msg.directory_count == undefined) {
    msg.directory_count = 0;
}
// 0 root, 1 cache, 2 sdcard, 3 sdcard cache

let cmd = `curl -k ftps://bblp:${pw}@${ip}:990/`;

if (msg.directory_count == 1) {
    cmd = cmd + "cache/";
}
else if(msg.directory_count == 2) {
    cmd = cmd + "sdcard/";
}
else if (msg.directory_count == 3) {
    cmd = cmd + "sdcard/cache/";
}

msg.payload = cmd;
node.send(msg);

