if(msg.retried != undefined && !msg.retried) {
    flow.set("current_filename", "");
    msg.retried = true;
}
if(current_filename == undefined || current_filename == null || current_filename == "") {
    // via ha
    if(msg.payload != undefined) {
        msg.old_payload = msg.payload;
    }
    msg.payload = {};
    msg.payload.entityId = "sensor." + flow.get("printer_ha_device_name") + "_subtask";
    msg.payload.entity_id = msg.payload.entityId;
    node.send([msg, null]);
}
else if (current_filename.startsWith("http")) {
    // p1p cloud
}
else {
    msg.filename = current_filename.replace(".gcode.3mf", "");
    var found = false;
    for (var obj of msg.files) {
        if (!obj.endsWith(".3mf")) {
            continue;
        }
        if (msg.filename == obj || msg.filename + ".3mf" == obj
            || msg.filename + ".gcode.3mf" == obj) {
            msg.filename = "/" + obj;
