            || "/sdcard/" + filename_str + ".gcode.3mf" == obj
            || "/sdcard" + filename_str == obj || "/sdcard" + filename_str + ".3mf" == obj
            || "/sdcard" + filename_str + ".gcode.3mf" == obj) {

            filename_str = obj;
            msg.payload = filename_str;
            node.send(msg)
            let msgText = "Found - " + msg.payload;
            node.status({ fill: "blue", shape: "ring", text: msgText + ` - ${dt}` });
            found = true;
            break;
        }
        else if ("/sdcard/cache/" + filename_str == obj || "/sdcard/cache/" + filename_str + ".3mf" == obj
            || "/sdcard/cache/" + filename_str + ".gcode.3mf" == obj
            || "/sdcard/cache" + filename_str == obj || "/sdcard/cache" + filename_str + ".3mf" == obj
            || "/sdcard/cache" + filename_str + ".gcode.3mf" == obj) {

