    && msg.previous_state != msg.current_state) {
    msg.payload = msg.current_state;
    if(msg.previous_state == "PAUSE" && msg.current_state == "RUNNING") {
        node.send([null, null, msg, null]);
        node.status({ fill: "blue", shape: "ring", text: `Print Resumed - ${dt}` });
    }
    else {

        if(msg.previous_state.toLowerCase() != "unknown"
        && msg.previous_state.toLowerCase() != "unavailable"
        && msg.previous_state != "OFFLINE" && msg.current_state != "OFFLINE"
        && msg.current_state.toLowerCase() != "unknown"
        && msg.current_state.toLowerCase() != "unavailable")
        {
            if (msg.previous_state != "PAUSE" && msg.current_state == "PAUSE"){
                // paused
                node.send([null, msg, null, null]);
                node.status({ fill: "blue", shape: "ring", text: `Print Paused - ${dt}` });
            }
            else if (msg.previous_state != "FAILED" && msg.current_state == "FAILED") {
                // failed or cancelled
                node.send([null, msg, null, {"machine_name": machine_name, "start_epoch": 0, "payload": {"entityId": entityId, "entity_id": entityId}}]);
                node.status({ fill: "blue", shape: "ring", text: `Print Cancelled or Failed - ${dt}` });
            }
            else if ( (msg.previous_state == "RUNNING" || msg.previous_state == "FINISH" || msg.previous_state == "FAILED") && msg.current_state == "IDLE") {
                // ended and is idle
                msg.payload = "FAILED";
                node.send([null, msg, null, {"machine_name": machine_name,  "start_epoch": 0, "payload": { "entityId": entityId, "entity_id": entityId } }]);
                node.status({ fill: "blue", shape: "ring", text: `Print Ended - ${dt}` });
            }
            else if (msg.previous_state != "FINISH" && msg.current_state == "FINISH") {
                // finished
                node.send([null, msg, null, {"machine_name": machine_name,  "start_epoch": 0, "payload": { "entityId": entityId, "entity_id": entityId } }]);
                node.status({ fill: "blue", shape: "ring", text: `Print Completed - ${dt}` });
            }
            else if (msg.current_state == "RUNNING"){
                // starts
                node.send([msg, null, null, null]);
                flow.set("print_progress_pct", 0)
                node.status({ fill: "blue", shape: "ring", text: `Print Started - ${dt}` });
            }
        }
    }
}
else {
    node.status({ fill: "white", shape: "ring", text: "Unhandled: " + msg.previous_state +" -> "+ msg.current_state });
    if (msg.current_state == "FINISH" || msg.current_state == "IDLE" || msg.current_state == "FAILED") {
        node.send([null, null, null, { "machine_name": machine_name, "start_epoch": 0, "payload": { "entityId": entityId, "entity_id": entityId } }]);
    }
