UPDATE prints SET
    filament_data = $1::jsonb
WHERE filament_data = '[]' and material_type = 'filament' and material is not null;

UPDATE prints SET
 filament_data = jsonb_set(filament_data, '{0, type}', to_json(material)::JSONB, true)
WHERE filament_data @> '[{"manual_set":true}]' and material_type = 'filament' and material is not null;

UPDATE prints SET
 filament_data = jsonb_set(filament_data, '{0, used_g}', to_json(material_used)::JSONB, true)
WHERE filament_data @> '[{"manual_set":true}]' and material_type = 'filament';

UPDATE prints SET
 filament_data = jsonb_set(filament_data, '{0, used_cost}', to_json(material_used_cost)::JSONB, true)
WHERE filament_data @> '[{"manual_set":true}]' and material_type = 'filament';

