if (msg.payload != undefined && msg.payload.rows != undefined 
    && msg.payload.rows.length > 0) {
    let minutes = 0;
    let data = {};
    for (var filament of msg.payload.rows) {
        if (filament['sum'] == undefined) {
            filament['sum'] = 0;
        }
        data[filament["material"]] = (Math.round((filament.sum / 3600) * 100) / 100).toString() + " h"
        minutes += filament.sum
    }
    minutes = (Math.round((minutes / 60) * 100) / 100)
    let hours = Math.round((minutes / 60) * 100) / 100; // yes again
    msg.payload = hours;
    msg.data = data;
    msg.topic = "active_print_time"
    msg.machine_name = flow.get("HA_DEVICE");
    node.send(msg);
    node.status({ fill: "blue", shape: "ring", text: `${hours} h` });
}

if (msg.payload != undefined && msg.payload.rows != undefined 
    && msg.payload.rows.length > 0) {
    let total = 0;
    let data = {};
    for (var filament of msg.payload.rows) {

        if (filament["material_unit"] == "g" && filament["sum"] > 0 && filament["material"] != undefined && filament["material"] != ""){
            total += filament["sum"];
            data[filament["material"]] = (Math.round((filament["sum"] / 1000) * 100) / 100).toString() + " kg";
        }
    }
    total = (Math.round((total) * 100) / 100);
    let kg_total = (Math.round((total / 1000) * 100) / 100);
    msg.payload = kg_total;
    msg.topic = "filament_printed"
    msg.machine_name = flow.get("HA_DEVICE");
    msg.data = data;
    node.send(msg);
    node.status({ fill: "blue", shape: "ring", text: `${kg_total} kg` });
}

let now = new Date();
let dt = now.toLocaleDateString("en-US", {"month": "short", "day": "numeric", "year": "numeric", "hour": "2-digit", "minute": "2-digit"});

let epoch = flow.get("RET_"+"print_start_epoch");
if (epoch != undefined && !isNaN(parseInt(epoch))) {
    epoch = parseInt(epoch);
}
else {
    epoch = 0;
}
msg['start_epoch'] = epoch;
if (epoch == 0) {
    node.send([null, msg]);
}
else {
    node.send([msg, null]);
}

node.status({ fill: "white", shape: "ring", text: `${epoch} - ${dt}` });
