function DecimalHexTwosComplement(decimal) {
    var size = 8;
    var hexadecimal;
    if (decimal >= 0) {
        hexadecimal = decimal.toString(16);

        while ((hexadecimal.length % size) != 0) {
            hexadecimal = "" + 0 + hexadecimal;
        }

        return hexadecimal;
    } else {
        hexadecimal = Math.abs(decimal).toString(16);
        while ((hexadecimal.length % size) != 0) {
            hexadecimal = "" + 0 + hexadecimal;
        }

        var output = '';
        for (var i = 0; i < hexadecimal.length; i++) {
            output += (0x0F - parseInt(hexadecimal[i], 16)).toString(16);
        }

        output = (0x01 + parseInt(output, 16)).toString(16);
        return output;
    }
}

let hms_data = {
    "HMS_Info": [],
    "HMS_Common": [],
    "HMS_Serious": [],
    "HMS_Fatal": [],
    "HMS_Unknown": []
};


let template = []
let count = 0;
let hms_data_dictionary = undefined;
if(msg.payload.print.hms != undefined){
    for (var hms_code of msg.payload.print.hms) {
        var attr = "";
        if(hms_code.attr != undefined && hms_code.attr != "") {
            attr=DecimalHexTwosComplement(hms_code.attr);
        }
        var code = "";
        if(hms_code.code != undefined && hms_code.code != "") {
            code = DecimalHexTwosComplement(hms_code.code);
        }

        let full_code = (attr + code).replace(/(.{4})/g, "$1_");
        full_code = full_code.substring(0, full_code.length - 1);
        let url = "https://wiki.bambulab.com/en/x1/troubleshooting/hmscode/"+full_code;

        let description = "";
        if (flow.get("hms_data") != undefined && hms_data_dictionary == undefined) {
            hms_data_dictionary = flow.get("hms_data");
        };

        if (hms_data_dictionary.hms[full_code.toLowerCase().replaceAll("_", "")] != undefined) {
            description = hms_data_dictionary.hms[full_code.toLowerCase().replaceAll("_", "")];
        }
        else if (hms_data_dictionary.error[full_code.toLowerCase().replaceAll("_", "")] != undefined) {
            description = hms_data_dictionary.error[full_code.toLowerCase().replaceAll("_", "")];
        }

        let module = ""; let type = "";
        if(flow.get("hms_module_parser") != undefined) {
            let module_parser = flow.get("hms_module_parser");
            module = module_parser(parseInt(hms_code.attr));        
        }
        if (flow.get("hms_level_parser") != undefined) {
            let level_parser = flow.get("hms_level_parser");
            type = level_parser(parseInt(hms_code.code));
        }
        if (type == "") {
            type = "Unknown";
        }

        let d = { "code": "HMS_" + full_code, "url": url, "description": description, "short_code": full_code.replaceAll("_", ""),
            "module": module, "type": type };

        hms_data["HMS_"+ type].push(d);
        count = count + 1;
        //template.push(d);
    }
   // msg.payload.print.hms = hms_data;
    delete msg.payload.print.hms;
    msg.payload.print["HMS_Unknown"] = hms_data["HMS_Unknown"];
    msg.payload.print["HMS_Info"] = hms_data["HMS_Info"];
    msg.payload.print["HMS_Common"] = hms_data["HMS_Common"];
    msg.payload.print["HMS_Serious"] = hms_data["HMS_Serious"];
    msg.payload.print["HMS_Fatal"] = hms_data["HMS_Fatal"];
    msg.payload.print["HMS_Notifications"] = {
        "Unknown": hms_data["HMS_Unknown"].length,
        "Info": hms_data["HMS_Info"].length,
        "Common": hms_data["HMS_Common"].length,
        "Serious": hms_data["HMS_Serious"].length,
        "Fatal": hms_data["HMS_Fatal"].length,
        "Total": count
    };
   //msg.payload.print.hms = template;
}
