    msg.payload.print_start_epoch = msg.payload.gcode_start_epoch;
    delete msg.payload.gcode_start_epoch;
}
node.send(msg);

function removeUseless(object, key) {
    if(object[key] != undefined) {
        delete object[key];
    }
}

let keys = [
    "command",
    "force_upgrade",
    "online",
    "ams_rfid_status",
    "ams_status",
    "manufacturer",
    "sequence_id",
    "filam_bak",
    "cali_version",
    "maintain",
    "fan_gear",
    "mess_production_state",
    "s_obj",
    "queue_est",
    "queue_sts",
    "fail_reason"
];
if(msg.payload != undefined) {
    for(var key of keys) {
        removeUseless(msg.payload, key);
    }
}
node.send(msg);

if(msg.payload.gcode_start_time == undefined) {
    if(msg.payload.gcode_end_time != undefined && msg.payload.mc_remaining_time == undefined) {
        delete msg.payload.gcode_end_time;
    }
    else if (msg.payload.gcode_end_time != undefined &&
        msg.payload.mc_remaining_time != undefined) {
        msg.payload.gcode_end_time = msg.payload.gcode_end_time - (msg.payload.gcode_end_time % 1000);
        var endTime = msg.payload.gcode_end_time + ((parseInt(msg.payload.mc_remaining_time) * 60) * 1000);
        endTime = Math.round(endTime / 60000) * 60000;
        msg.payload.gcode_end_time = endTime;
    }
    node.send(msg);
}
else {
    if (msg.payload.gcode_start_time !== "N/A" && msg.payload.gcode_end_time !== undefined && msg.payload.gcode_end_time != "N/A"
        && msg.payload.mc_remaining_time != undefined) {
        var endTime = msg.payload.gcode_end_time + ((parseInt(msg.payload.mc_remaining_time) * 60) * 1000);
        endTime = Math.round(endTime / 60000) * 60000;
        var startSeconds = msg.payload.gcode_start_epoch.toString().slice(0,-5);
        endTime = endTime + parseInt(startSeconds);
        msg.payload.gcode_end_time = endTime;
        msg.payload.gcode_start_time = parseInt(msg.payload.gcode_start_time);
        node.send(msg);
    }
    else if (msg.payload.gcode_end_time == "N/A" || msg.payload.gcode_start_Time == "N/A") {
        msg.payload.gcode_start_time = "N/A";
        msg.payload.gcode_end_time = "N/A";
        node.send(msg);
    }

    if (msg.payload.gcode_start_time == "N/A" || msg.payload.mc_remaining_time == undefined) {
        msg.payload.gcode_end_time = "N/A";
        node.send(msg);
    }
    else {
        node.send(msg);
    }
}

if(msg.payload.gcode_state != undefined && 
   msg.payload.gcode_start_time != undefined) {

    if (msg.payload.gcode_state == "IDLE" ||
        msg.payload.gcode_start_time == "0" || msg.payload.gcode_start_time == "N/A") {
        msg.payload.gcode_start_time = "N/A";
        msg.payload.gcode_end_time = "N/A";
    }
    else {
        msg.payload.gcode_start_time = msg.payload.gcode_start_time + "000";
    }
}
else if (msg.payload.gcode_start_time != undefined && 
    msg.payload.gcode_start_time != "0") {
    msg.payload.gcode_start_time = msg.payload.gcode_start_time + "000"; 
}
node.send(msg);

if (msg.payload.gcode_start_time != undefined) {
    msg.payload.gcode_start_epoch = msg.payload.gcode_start_time;
}
node.send(msg);

msg.topic = flow.get("model");//_" + msg.payload.id;

msg.machine_name = msg.topic + "_" + msg.payload.machine_name;
