function getHMSLevel(code) {
    let uint_id = (code >> 16);
    switch (uint_id) {
        case 1:
            return "Fatal";
        case 2:
            return "Serious";
        case 3:
            return "Common";
        case 4:
            return "Info";
        default:
            return "Unknown";
    }
}

function getHMSModule(attr) {
    let uint_id = ((attr >> 24) & 0xFF);
    switch (uint_id) {
        case 0x05:
            return "MainBoard";
        case 0x0C:
            return "XCam";
        case 0x07:
            return "AMS";
        case 0x08:
            return "Toolhead";
        case 0x03:
            return "MC";
        default:
            return "Unknown";
    }
}

