    }
    flow.set("is_printer_reachable", true);
    node.status({ fill: "green", shape: "ring", text: "Printer IP is online" });
} else {
    was_ping_killed = false;
    // TODO IF PING KILLED 
    if(flow.get("is_printer_reachable")) {
        if (was_ping_killed) {
            // if ping command killed
            node.warn("Ping command was unexpectedly killed");
        }
        else {
            node.warn("Printer " + flow.get("printer_name") + " is not reachable!");
        }

    }
    if (was_ping_killed) {
        // if ping command killed
        node.status({fill: "red", shape: "ring", text: "Ping command was unexpectedly killed"});
