    var oldTopic = msg.topic;
    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    let oldPayload = msg.payload;
    msg.payload = { "Serial Number": flow.get("printer_serial")};
    node.send(msg);
    msg.payload = oldPayload;
    msg.topic = oldTopic;
}
