    hasAttr = true;
    msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
    node.send(msg);
    msg.topic = oldTopic;
    msg.payload = msg.payload.status;
}
else if (msg.topic == "upload") {
