    hasAttr = true;
    if (oldTopic != "HMS_Notifications"){
        msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
        numOfCodes = msg.payload == undefined || msg.payload == [] ? 0 : msg.payload.length;
        var links = {};
        for (var data of msg.payload) {
            //if (data.type != undefined && data.type != "") {
            //    links[data.code.replaceAll(" ", "_") + " Severity"] = data.type;
            //}
            if ( data.description != undefined && data.description != "") {
                links[data.code.replaceAll(" ", "_")+" Description"] = data.description;
            }
            if(data.module != undefined && data.module != "") {
                links[data.code.replaceAll(" ", "_") + " Module"] = data.module;
            }

            links[data.code.replaceAll(" ", "_")] = data.url;
        }
        msg.payload = links;
        if(msg.payload != undefined && msg.payload != "" && msg.payload != {}) {
            node.send(msg);
        }
        msg.topic = oldTopic;
        msg.payload = numOfCodes;
    }
    else {
        numOfCodes = msg.payload["Total"];
        delete msg.payload["Total"];
        msg.topic = "HMS";
        if (msg.payload != undefined && msg.payload != "" && msg.payload != {}) {
            msg.topic = root_topic + "/sensor/" + ha_device_name + "/" + msg.topic + "/attr";
            node.send(msg);
        }
        msg.topic = "HMS";
        msg.payload = numOfCodes;
    }
}
