let time = "";
let state = msg.payload;
if (state != undefined && state == "RUNNING") {
    time = msg.gcode_start_time.toString();
}
else if (state != undefined && state == "IDLE") {
    time = "0";
}
else {
    return;
}

if (time != "") {
    msg.payload = {
        "print": {
            "gcode_start_time": time,
            "gcode_state": state
        }
    };

    msg.topic = "";
    node.send(msg);
    node.status({ fill: "blue", shape: "ring", text: "Set Time: " + time });
}
